import * as actionTypes from './actions';

const initialState = {
  ingredients: null,
  totalPrice: 4
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case actionType.ADD_INGREDIENT:
      return {};
    case actionType.REMOVE_INGREDIENT:
      return {};
    default:
      return state;
  }
};

export default reducer;
